import React from "react";

import countries from "../../../common/forms/commons/countries";

import SearchDropdown from "./Dropdown";

export default class SearchForm extends React.Component {

  constructor(props) {
    super(props);
    this.dropdownList = [
      label,
      name,
      id,
      checked
    ];
  }

  static defaultProps = {
    countries
  };

  onSubmit(e) {
    e.preventDefault();
    console.log("checkout submit stuff");
  }

  createCountryList() {
    this.props.dropdownList = this.props.countries.map((item, key) => {
          label = item.value;
          name = item.value;
          id = item.key;
          checked = true;
      });        
  }

  render() {
    return ( 
      <form
        id="checkout-form"
        className="smart-form"
        noValidate="novalidate"
        onSubmit={this.onSubmit}
      >
        <fieldset>
          <div className="row">
            <header>
                <span className="widget-icon">
                <i className="fa fa-edit" />
                </span>

                <h2>Search Filters </h2>
            </header>
            <section className="col col-12">
            <label
                className="control-label"
                htmlFor="multiselect1"
                >
                Country
            </label>
            createCountryList()

            <SearchDropdown list={this.props.dropdownList} ></SearchDropdown> 
            
            </section>
          </div>
        <div className="row">        
        <section className="col col-4">
              <label className="input">
                <input type="text" name="city" placeholder="City" />
              </label>
        </section>
        </div>
        <div className="row">
        <section className="col col-3">
              <label className="input">
                <input type="text" name="code" placeholder="Post code" />
              </label>
            </section>
        </div>       
        </fieldset>

        <footer>
          <button type="submit" className="btn btn-primary">
            Validate Form
          </button>
        </footer>
      </form>
    );
  }
}
