import React from "react";

import $ from "jquery";
import axios from "axios";

import countries from "../../../common/forms/commons/countries";

import SearchDropdown from "./Dropdown";

export default class SearchForm extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      error: null,
      isLoaded: false,
      countryList: []
    }
  }


  static defaultProps = {
    countries
  };

  onSubmit(e) {
    e.preventDefault();
    console.log("checkout submit stuff");
  }

  formatCountry(data) {
   var temp_list = [];

   $.each(data , (count, item) => {
    
    console.log(item)

    temp_list.push({
      label: item.name,
      name: item.name,
      id: item.code,
      checked: false
    })

   });

   this.setState({
    countryList : temp_list
   })
  }

  componentDidMount() {
    axios.get('https://api.openaq.org/v1/countries')
    .then((response) => {

      this.setState({
        isLoaded: true
      });

      this.formatCountry(response.data.results)
    })
    .catch((error) => {
      this.setState({
          isLoaded: true,
          error
        });
    })
    .then(() => {
      // always executed
    });
  }

  render() {

    console.log(this.state.countryList)

    return ( 
      <form
        id="checkout-form"
        className="smart-form"
        noValidate="novalidate"
        onSubmit={this.onSubmit}
      >
        <fieldset>
          <div className="row">
            <header>
                <span className="widget-icon">
                <i className="fa fa-edit" />
                </span>

                <h2>Search Filters </h2>
            </header>
            <section className="col col-12">
            <label
                className="control-label"
                htmlFor="multiselect1"
                >
                Country
            </label>
             
            </section>
          </div>
        <div className="row">        
        <section className="col col-4">
              <label className="input">
                <input type="text" name="city" placeholder="City" />
              </label>
        </section>
        </div>
        <div className="row">
        <section className="col col-3">
              <label className="input">
                <input type="text" name="code" placeholder="Post code" />
              </label>
            </section>
        </div>       
        </fieldset>

        <footer>
          <button type="submit" className="btn btn-primary">
            Validate Form
          </button>
        </footer>
      </form>
    );
  }
}
